$(document).ready(function () {
  $(".slick").slick({
    dots: true,
    infinite: false,
    speed: 300,
    centerPadding: "60px",
    arrows: true, // Set arrows to false to remove arrow navigation
    slidesToShow: 4,
    slidesToScroll: 4,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
          arrows: true,
          dots: true,
        },
      },
      {
        breakpoint: 769,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          arrows: false, // Remove arrows on screens smaller than 768px
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          arrows: true,
        }
      },
    ],
  });
});

